    const [weights, setWeights] = useState([]);
    const user = getUser();

    useEffect(() => {
        fetch(`/weights/${user.id}`)
            .then((res) => res.json())
            .then((weights) => setExercises(weights));
    }, [user.id]);

    return (
        <div>
            <button
                type="button"
                class="btn btn-primary btn-block"
                data-toggle="modal"
                data-target="#addExercise"
            >
                Add Exercise to Track
            </button>


        </div>
    );
}
